import React, { Component } from 'react';
import Modal from './Modal';
import axios from 'axios';

export default class EditUser extends Component {
    constructor(props) {
        super(props);
        this.state = {
              email: '',
              last_name: '',
              first_name: '',
              gender: '',
           }
        this.onChangeHostemail = this.onChangeHostemail.bind(this);
        this.onChangeLname = this.onChangeLname.bind(this);
        this.onChangeFname = this.onChangeFname.bind(this);
        this.onChangeHostGender = this.onChangeHostGender.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        
    }
    onChangeHostemail(e) {
        this.setState({
            new_email: e.target.value
        });
    }

    onChangeHostGender(e) {
        this.setState({
            new_gender: e.target.value
        });
    }
    onChangeFname(e) {
        this.setState({
            new_fname: e.target.value
        });
    }
    onChangeLname(e) {
        this.setState({
            new_lname: e.target.value
        });
    }
    componentDidMount() {
        let token = localStorage.getItem('token');
        let type = localStorage.getItem('type');
        let authToken = type + ' ' + token;
        var url = this.props.location;
        let id = this.props.location.search;
        id = id.split('?')[1];
        this.setState({
            user_id: id,
        });



        axios.get(
            'http://192.168.6.234:3000/users/' + id,
            {
                headers: {
                    "Access-Control-Allow-Origin": "*",
                    "Authorization": authToken
                }
            }
        ).then(
            // if (token) {
            res => {
                if (res.data) {
                    // serverports.splice(index, 1);
                    // this.setState({ ...this.state, serverports })
                    console.log("before1");
                    this.setState({
                        fname: res.data.data.user.first_name,
                        lname: res.data.data.user.last_name,
                        gender: res.data.data.user.gender,
                        email: res.data.data.user.email
                    });
                    console.log(this.state);
                    console.log("after1");
                }
            }
        );
    }



    onSubmit(e) {
        e.preventDefault();
        const serverport = {
            email: this.state.new_email,
            gender: this.state.new_gender,
            first_name: this.state.new_fname,
            last_name: this.state.new_lname

        }



        // let token = localStorage.getItem('token');
        // let type = localStorage.getItem('type');
        // let authToken = type + ' ' + token;
        // let id = this.state.user_id;


        let token = localStorage.getItem('token');
        let type = localStorage.getItem('type');
        let authToken = type + ' ' + token;
        var url = this.props.location;
        let id = this.props.location.search;
        id = id.split('?')[1];
        this.setState({
            user_id: id,
        });


        axios.post(
            'http://192.168.6.234:3000/users/' + id,
            {
                headers: {
                    "Access-Control-Allow-Origin": "*",
                    "Authorization": authToken
                }
            }, serverport
        ).then(
            // if (token) {
            res => {
                if (res.data) {
                    // serverports.splice(index, 1);
                    // this.setState({ ...this.state, serverports })
                    console.log("before2");
                    this.setState({
                        fname: res.data.data.user.first_name,
                        lname: res.data.data.user.last_name,
                        gender: res.data.data.user.gender,
                        email: res.data.data.user.email
                    });
                    console.log("after2");
                    console.log(this.state);
                }
            }
        );

        // this.setState({
        //     email: '',
        //     password: ''
        // });
    }


    render() {
        return (
            <div style={{ marginTop: 50 }}>
                <div className="container ">
                    <div className="row border-area">
                        <div className="col-xs-10 col-sm-10 col-md-10 col-lg-10">
                            <h3>Edit User</h3>
                            <form onSubmit={this.onSubmit}>
                                <div className="form-group">
                                    <label>First Name:  </label>
                                    <input type="text" className="form-control" onChange={this.onChangeFname} placeholder="Please enter first name" value={this.state.fname} />
                                </div>
                                <div className="form-group">
                                    <label>Last Name:  </label>
                                    <input type="text" className="form-control" onChange={this.onChangeLname} placeholder="Please enter last name" value={this.state.lname} />
                                </div>
                                <div className="form-group">
                                    <label>Gender: </label>
                                    <select onChange={this.onChangeHostGender} className="form-control" value={this.state.gender}>
                                        <option value="">---Please select the gender---</option>
                                        <option value="male">Male</option>
                                        <option value="female">Female</option>
                                    </select>
                                </div>
                                <div className="form-group">
                                    <label>Email:  </label>
                                    <input type="text" className="form-control" onChange={this.onChangeHostemail} placeholder="Please enter Email" value={this.state.email} />
                                </div>
                                <div className="form-group">
                                    <input type="submit" value="Update" className="btn btn-primary" />
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
